import React from 'react';
import classes from './Topbar.module.css';
import { Link, useLocation } from 'react-router-dom';


const Topbar = (props) => {
    const currentLocation = useLocation().pathname;
    return (
        <div className={classes.Topbar}>
            <div className={classes.LeftMenu}>
                <div className={classes.LogoWrapper}>
                    <img src="https://edu-web-fundamentals.web.app/static/media/logo.58169365.png" alt="Logo" />
                    <p className={classes.BrandName}>Kafene</p>
                </div>
                <nav>
                    <Link className={[classes.MenuItem, currentLocation.includes('orders') ? classes.Active : null].join(' ')} >Orders</Link>
                    <Link className={[classes.MenuItem, currentLocation.includes('products') ? classes.Active : null].join(' ')} >Products</Link>
                    <Link className={[classes.MenuItem, currentLocation.includes('users') ? classes.Active : null].join(' ')} >Users</Link>
                </nav>
            </div>

            {
                props.loginStatus ? 
                <Link className={classes.MenuItem} onClick={props.onUserLoggedOut}>Logout</Link>
                :
                null
            }
        </div>
    );
}

export default Topbar;
